<script>function toggleContentWithAnimation(){topContent.style.opacity=0,setTimeout(()=>{topContent.innerHTML=contents[currentContentIndex],topContent.style.opacity=1,currentContentIndex=(currentContentIndex+1)%contents.length},500)}function autoToggleContent(){setInterval(toggleContentWithAnimation,3e3)}function startCountdown(e,t){let n,o,a=e;setInterval(function(){n=parseInt(a/60,10),o=parseInt(a%60,10),n=n<10?"0"+n:n,o=o<10?"0"+o:o,t.textContent=n+":"+o,--a<0&&(a=e)},1e3)}function updateSelectedThumbnail(){const e=document.querySelectorAll(".thumbnail");e.forEach((e,t)=>{t===currentImageIndex?e.classList.add("selected"):e.classList.remove("selected")})}function prevImage(){currentImageIndex>0?currentImageIndex--:currentImageIndex=images.length-1,displayImage(images[currentImageIndex])}function nextImage(){currentImageIndex<images.length-1?currentImageIndex++:currentImageIndex=0,displayImage(images[currentImageIndex])}function displayImage(e){document.getElementById("mainImage").src=e,updateSelectedThumbnail()}function getNextDate(){const e=["Sunday","Monday","Tuesday","Wednesday","Thursday","Friday","Saturday"],t=["January","February","March","April","May","June","July","August","September","October","November","December"],n=new Date,o=new Date(n);o.setDate(n.getDate()+2);const a=e[o.getDay()],r=o.getDate(),c=t[o.getMonth()];return{day:a,date:r,month:c}}function updateShippingDate(){const e=getNextDate(),t=document.querySelector(".dateDay"),n=document.querySelector(".dateNumber"),o=document.querySelector(".monthName");t.textContent=e.day,n.textContent=e.date,o.textContent=e.month}function selectThumbnail(e,t){var n=document.querySelectorAll(".option");n.forEach(function(e){e.style.border="none"}),e.style.border="2px solid black";var o=document.getElementById("mainImage");o.src=t,updateSelectedThumbnail()}const contents=[];let currentContentIndex=0;const topContent=document.getElementById("topContent");autoToggleContent(),window.onload=function(){const e=1800,t=document.querySelector("#countdownTimer");startCountdown(e,t)};const images=[];let currentImageIndex=0;updateShippingDate(),window.addEventListener("scroll",function(){var e=document.querySelector(".disappear"),t=document.querySelector(".fixed"),n=e.getBoundingClientRect();n.bottom<=0||n.top>=window.innerHeight?t.style.display="block":t.style.display="none"});
</script>